// src/app/App.jsx
import { useMemo, useState } from "react";
import Header from "../components/Header";
import SearchBar from "../components/SearchBar";
import SegmentedControl from "../components/SegmentedControl";
import CourtDrawer from "../components/CourtDrawer";
import CourtMap from "../features/courts/CourtMap";
import CourtList from "../features/courts/CourtList";
import { useCourts } from "../features/courts/useCourts";

export default function App() {
  const [view, setView] = useState("Map"); // "Map" | "List"
  const [selectedId, setSelectedId] = useState(null);

  const [query, setQuery] = useState("");

  const { courts, computedById } = useCourts();

  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return courts;

    return courts.filter((c) => {
      const hay = `${c.name} ${c.city} ${c.island} ${c.address}`.toLowerCase();
      return hay.includes(q);
    });
  }, [courts, query]);

  const selected = useMemo(
    () => courts.find((c) => c.id === selectedId) || null,
    [courts, selectedId]
  );

  return (
    <>
      <Header />

      <div className="container">
        <div className="card">
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              gap: "12px",
              marginBottom: "15px",
              alignItems: "center",
              flexWrap: "wrap",
            }}
          >
            <div style={{ flex: "1 1 320px" }}>
              <SearchBar value={query} onChange={setQuery} />
            </div>

            <div style={{ flex: "0 0 auto" }}>
              <SegmentedControl value={view} onChange={setView} />
            </div>
          </div>

          {view === "Map" ? (
            <CourtMap
              courts={filtered}
              computedById={computedById}
              selectedId={selectedId}
              onSelect={setSelectedId}
            />
          ) : (
            <CourtList
              courts={filtered}
              computedById={computedById}
              selectedId={selectedId}
              onSelect={setSelectedId}
            />
          )}
        </div>
      </div>

      <CourtDrawer
        open={!!selected}
        court={selected}
        computed={selected ? computedById[selected.id] : null}
        onClose={() => setSelectedId(null)}
      />
    </>
  );
}
