import Pill from "./Pill";
import { MapPin, Users, Clock, Star } from "lucide-react";

export default function CourtCard({ court, meta, selected, onClick }) {
  const tone = meta?.isOpenNow ? "good" : meta?.nextStart ? "cool" : "neutral";

  return (
    <button
      onClick={onClick}
      className={`court-item ${selected ? "selected" : ""}`}
      style={{ width: "100%", textAlign: "left" }}
    >
      <div style={{ display: "flex", justifyContent: "space-between", gap: 12 }}>
        <div>
          <div style={{ fontWeight: 800 }}>{court.name}</div>
          <div style={{ fontSize: 13, color: "#64748b", marginTop: 6, display: "flex", gap: 6, alignItems: "center" }}>
            <MapPin size={14} />
            {court.city} â€¢ {court.island}
          </div>
        </div>

        <Pill tone={tone}>
          {meta?.isOpenNow ? (
            <>
              <Users size={14} /> Open now
            </>
          ) : meta?.nextStart ? (
            <>
              <Clock size={14} /> Next: {meta.nextStartLabel}
            </>
          ) : (
            "Schedule TBD"
          )}
        </Pill>
      </div>

      <div style={{ marginTop: 10, display: "flex", gap: 8, flexWrap: "wrap" }}>
        <Pill tone="neutral">{court.courts} courts</Pill>
        <Pill tone="neutral">{court.surface}</Pill>
        {court.lights ? <Pill tone="warn">Lights</Pill> : <Pill tone="neutral">No lights</Pill>}
        {court.covered ? <Pill tone="hot">Covered</Pill> : <Pill tone="neutral">Outdoor</Pill>}
        <Pill tone="neutral">
          <Star size={14} /> {Number(court.communityScore).toFixed(1)}
        </Pill>
      </div>
    </button>
  );
}
