import { useMemo, useState } from "react";
import { X, MapPin, ExternalLink, Bell, MessageSquarePlus } from "lucide-react";
import Pill from "./Pill";
import { toggleFavorite, isFavorite, saveFavorite } from "../utils/storage";

export default function CourtDrawer({ open, court, computed, onClose }) {
  const [note, setNote] = useState("");

  const fav = useMemo(() => (court ? isFavorite(court.id) : false), [court]);

  if (!open || !court) return null;

  const statusTone = computed?.isOpenNow
    ? "good"
    : computed?.nextStart
    ? "cool"
    : "neutral";

  const statusText = computed?.isOpenNow
    ? "Open play happening now"
    : computed?.nextStart
    ? `Next open play: ${computed.nextStartLabel}`
    : "Open play schedule: TBD";

  return (
    <div className="fixed inset-0 z-[999]">
      <div
        className="absolute inset-0 bg-black/30 backdrop-blur-sm"
        onClick={onClose}
      />
      <div className="absolute right-0 top-0 h-full w-full max-w-xl bg-white shadow-soft border-l border-slate-200">
        <div className="p-5 border-b border-slate-100 flex items-start justify-between gap-3">
          <div>
            <div className="text-xl font-extrabold tracking-tight">
              {court.name}
            </div>
            <div className="text-sm text-slate-600 mt-1 flex items-center gap-2">
              <MapPin size={16} />
              {court.address}
            </div>
            <div className="mt-3">
              <Pill tone={statusTone}>{statusText}</Pill>
            </div>
          </div>

          <button
            className="h-10 w-10 rounded-2xl border border-slate-200 grid place-items-center hover:bg-slate-50"
            onClick={onClose}
            aria-label="Close"
          >
            <X />
          </button>
        </div>

        <div className="p-5 space-y-5 overflow-auto h-[calc(100%-76px)]">
          <section className="rounded-3xl border border-slate-200 bg-gradient-to-br from-indigo-50 to-pink-50 p-4">
            <div className="font-bold mb-2">Quick actions</div>
            <div className="flex flex-wrap gap-2">
              <button
                onClick={() => {
                  toggleFavorite(court.id);
                  saveFavorite();
                  window.dispatchEvent(new Event("storage"));
                }}
                className="px-4 py-2 rounded-2xl bg-white border border-slate-200 shadow-soft font-semibold text-sm hover:bg-slate-50"
              >
                {fav ? "★ Favorited" : "☆ Favorite"}
              </button>

              <button
                onClick={() =>
                  alert("Mock alert: would subscribe to push notifications here.")
                }
                className="px-4 py-2 rounded-2xl bg-white border border-slate-200 shadow-soft font-semibold text-sm hover:bg-slate-50 flex items-center gap-2"
              >
                <Bell size={16} />
                Notify me
              </button>

              <a
                className="px-4 py-2 rounded-2xl bg-white border border-slate-200 shadow-soft font-semibold text-sm hover:bg-slate-50 flex items-center gap-2"
                href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(
                  court.address
                )}`}
                target="_blank"
                rel="noreferrer"
              >
                <ExternalLink size={16} />
                Open in Maps
              </a>
            </div>
          </section>

          <section className="rounded-3xl border border-slate-200 bg-white p-4">
            <div className="font-bold mb-2">Open play schedule (sample)</div>
            <div className="space-y-2">
              {court.openPlay.map((s, idx) => (
                <div
                  key={idx}
                  className="flex items-center justify-between rounded-2xl border border-slate-100 p-3"
                >
                  <div className="text-sm font-semibold">{s.day}</div>
                  <div className="text-sm text-slate-700">
                    {s.start}–{s.end}
                  </div>
                  <div className="text-xs text-slate-500">{s.level}</div>
                </div>
              ))}
            </div>
            <div className="text-xs text-slate-500 mt-3">
              This app is running on sample data — add your real court list +
              schedules later.
            </div>
          </section>

          <section className="rounded-3xl border border-slate-200 bg-white p-4">
            <div className="font-bold mb-2 flex items-center gap-2">
              <MessageSquarePlus size={18} />
              Community update (mock)
            </div>
            <textarea
              value={note}
              onChange={(e) => setNote(e.target.value)}
              placeholder="e.g., 'Open play is packed today — 10+ waiting' or 'Nets are up, 6 players here now!'"
              className="w-full min-h-[90px] rounded-2xl border border-slate-200 p-3 text-sm outline-none"
            />
            <button
              onClick={() => {
                alert(
                  "Mock submit: would store this in a database + show a live crowd status."
                );
                setNote("");
              }}
              className="mt-3 px-4 py-2 rounded-2xl bg-gradient-to-br from-indigo-500 to-pink-400 text-white font-bold shadow-soft hover:opacity-95"
            >
              Post update
            </button>
          </section>
        </div>
      </div>
    </div>
  );
}
