import { useEffect, useMemo } from "react";
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from "react-leaflet";
import { fixLeafletIcons } from "../../utils/leafletIcons";

export default function CourtMap({ courts, computedById, selectedId, onSelect }) {
  useEffect(() => { fixLeafletIcons(); }, []);

  const center = useMemo(() => {
    if (!courts.length) return [21.35, -157.95];
    return [courts[0].lat, courts[0].lng];
  }, [courts]);

  return (
    <div className="card" style={{ padding: 12 }}>
      <MapContainer center={center} zoom={10} scrollWheelZoom className="leaflet-container">
        <TileLayer
          attribution="&copy; OpenStreetMap contributors"
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {courts.map((c) => {
          const meta = computedById[c.id];
          const isSelected = selectedId === c.id;
          const openNow = !!meta?.isOpenNow;

          return (
            <Marker
              key={c.id}
              position={[c.lat, c.lng]}
              eventHandlers={{ click: () => onSelect(c.id) }}
            >
              <Popup>
                <div style={{ minWidth: 220 }}>
                  <div style={{ fontWeight: 1000 }}>{c.name}</div>
                  <div style={{ color: "#64748b", marginTop: 4, fontSize: 13 }}>
                    {c.city} • {c.island}
                  </div>
                  <div style={{ marginTop: 8, fontSize: 13 }}>
                    {openNow ? "✅ Open now" : meta?.nextStartLabel ? `⏭️ ${meta.nextStartLabel}` : "ℹ️ TBD"}
                  </div>
                </div>
              </Popup>

              <CircleMarker
                center={[c.lat, c.lng]}
                radius={isSelected ? 14 : 10}
                pathOptions={{
                  color: openNow ? "#10b981" : "#6366f1",
                  fillColor: openNow ? "#34d399" : "#a5b4fc",
                  fillOpacity: 0.85,
                  weight: isSelected ? 5 : 3,
                }}
              />
            </Marker>
          );
        })}
      </MapContainer>

      <div className="p" style={{ marginTop: 10 }}>
        Tip: click a marker to open the court drawer.
      </div>
    </div>
  );
}
