import clsx from "clsx";

export default function SegmentedControl({ value, onChange, options }) {
  return (
    <div className="inline-flex rounded-2xl bg-white border border-slate-200 p-1 shadow-soft">
      {options.map((opt) => (
        <button
          key={opt}
          onClick={() => onChange(opt)}
          className={clsx(
            "px-4 py-2 rounded-xl text-sm font-semibold transition",
            value === opt
              ? "bg-gradient-to-br from-indigo-500 to-pink-400 text-white shadow-soft"
              : "text-slate-700 hover:bg-slate-50"
          )}
        >
          {opt}
        </button>
      ))}
    </div>
  );
}
