import clsx from "clsx";

export default function Pill({ children, tone = "neutral" }) {
  const styles = {
    neutral: "bg-white border-slate-200 text-slate-700",
    good: "bg-emerald-50 border-emerald-200 text-emerald-800",
    warn: "bg-amber-50 border-amber-200 text-amber-800",
    hot: "bg-pink-50 border-pink-200 text-pink-800",
    cool: "bg-indigo-50 border-indigo-200 text-indigo-800",
  };

  return (
    <span
      className={clsx(
        "inline-flex items-center gap-1 px-3 py-1 rounded-full text-xs font-bold border",
        styles[tone] || styles.neutral
      )}
    >
      {children}
    </span>
  );
}
