import { useEffect, useMemo } from "react";
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from "react-leaflet";
import { fixLeafletIcons } from "../../utils/leafletIcons";

export default function CourtMap({ courts, computedById, selectedId, onSelect }) {
  useEffect(() => {
    fixLeafletIcons();
  }, []);

  const center = useMemo(() => {
    // Default: Oʻahu-ish
    if (!courts.length) return [21.35, -157.95];
    const c = courts[0];
    return [c.lat, c.lng];
  }, [courts]);

  return (
    <div className="grid gap-4 lg:grid-cols-[1.35fr_1fr]">
      <div className="rounded-3xl bg-white/70 border border-white shadow-soft p-3">
        <MapContainer center={center} zoom={10} scrollWheelZoom className="h-[420px] w-full">
          <TileLayer
            attribution="&copy; OpenStreetMap contributors"
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {courts.map((c) => {
            const meta = computedById[c.id];
            const isSelected = selectedId === c.id;
            const openNow = !!meta?.isOpenNow;

            return (
              <Marker
                key={c.id}
                position={[c.lat, c.lng]}
                eventHandlers={{ click: () => onSelect(c.id) }}
              >
                <Popup>
                  <div className="min-w-[220px]">
                    <div className="font-extrabold">{c.name}</div>
                    <div className="text-sm text-slate-600">
                      {c.city} • {c.island}
                    </div>
                    <div className="text-sm mt-2">
                      {openNow
                        ? "✅ Open now"
                        : meta?.nextStartLabel
                        ? `⏭️ ${meta.nextStartLabel}`
                        : "ℹ️ TBD"}
                    </div>
                  </div>
                </Popup>

                {/* cute glow dot */}
                <CircleMarker
                  center={[c.lat, c.lng]}
                  radius={isSelected ? 14 : 10}
                  pathOptions={{
                    color: openNow ? "#10b981" : "#6366f1",
                    fillColor: openNow ? "#34d399" : "#a5b4fc",
                    fillOpacity: 0.85,
                    weight: isSelected ? 5 : 3,
                  }}
                />
              </Marker>
            );
          })}
        </MapContainer>
      </div>

      <div className="rounded-3xl bg-white/70 border border-white shadow-soft p-4">
        <div className="font-extrabold tracking-tight text-lg">Quick list</div>
        <div className="text-sm text-slate-600 mt-1">
          Click a dot or a row to see details.
        </div>

        <div className="mt-4 space-y-2">
          {courts.slice(0, 6).map((c) => {
            const meta = computedById[c.id];
            return (
              <button
                key={c.id}
                onClick={() => onSelect(c.id)}
                className={`w-full rounded-2xl border px-3 py-2 text-left transition ${
                  selectedId === c.id
                    ? "border-indigo-300 bg-white shadow-soft"
                    : "border-slate-200 bg-white/80 hover:bg-white"
                }`}
              >
                <div className="font-bold text-sm">{c.name}</div>
                <div className="text-xs text-slate-600 mt-1">
                  {meta?.isOpenNow ? "Open now" : meta?.nextStartLabel ? `Next: ${meta.nextStartLabel}` : "Schedule TBD"}
                </div>
              </button>
            );
          })}
          {!courts.length && (
            <div className="text-sm text-slate-600">No courts match your filters.</div>
          )}
        </div>
      </div>
    </div>
  );
}
