export default function SegmentedControl({ value, onChange }) {
  return (
    <div style={{ display: "flex", gap: "10px" }}>
      <button
        className={`button ${value === "Map" ? "button-primary" : "button-outline"}`}
        onClick={() => onChange("Map")}
      >
        Map
      </button>

      <button
        className={`button ${value === "List" ? "button-primary" : "button-outline"}`}
        onClick={() => onChange("List")}
      >
        List
      </button>
    </div>
  );
}
