import { useEffect, useMemo, useState } from "react";
import { COURTS } from "./courtsData.js";
import { computeCourtStatus } from "../../utils/time";

export function useCourts() {
  const [tick, setTick] = useState(0);

  // Update “open now” every minute
  useEffect(() => {
    const t = setInterval(() => setTick((x) => x + 1), 60_000);
    return () => clearInterval(t);
  }, []);

  const computedById = useMemo(() => {
    const map = {};
    for (const c of COURTS) map[c.id] = computeCourtStatus(c);
    return map;
  }, [tick]);

  return { courts: COURTS, computedById };
}
