import { useMemo, useState } from "react";
import Header from "../components/Header";
import SearchBar from "../components/SearchBar";
import FilterChips from "../components/FilterChips";
import SegmentedControl from "../components/SegmentedControl";
import CourtDrawer from "../components/CourtDrawer";
import CourtMap from "../features/courts/CourtMap";
import CourtList from "../features/courts/CourtList";
import { useCourts } from "../features/courts/useCourts.js";

export default function App() {
  const [view, setView] = useState("Map");
  const [selectedId, setSelectedId] = useState(null);

  const [query, setQuery] = useState("");
  const [island, setIsland] = useState("All");
  const [openNowOnly, setOpenNowOnly] = useState(false);
  const [skill, setSkill] = useState("All");

  const { courts, computedById } = useCourts();

  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    return courts.filter((c) => {
      if (island !== "All" && c.island !== island) return false;
      if (skill !== "All" && !c.skillLevels.includes(skill)) return false;

      const meta = computedById[c.id];
      if (openNowOnly && !meta?.isOpenNow) return false;

      if (!q) return true;
      const hay = `${c.name} ${c.city} ${c.island} ${c.address}`.toLowerCase();
      return hay.includes(q);
    });
  }, [courts, computedById, query, island, openNowOnly, skill]);

  const selected = useMemo(
    () => courts.find((c) => c.id === selectedId) || null,
    [courts, selectedId]
  );

  return (
    <div className="min-h-full">
      <Header />

      <main className="mx-auto max-w-6xl px-4 pb-10">
        <div className="mt-6 grid gap-4">
          <div className="rounded-3xl bg-white/70 backdrop-blur border border-white shadow-soft p-4">
            <div className="flex flex-col gap-3 md:flex-row md:items-center md:justify-between">
              <SearchBar value={query} onChange={setQuery} />
              <SegmentedControl
                value={view}
                onChange={setView}
                options={["Map", "List"]}
              />
            </div>

            <div className="mt-3">
              <FilterChips
                island={island}
                setIsland={setIsland}
                openNowOnly={openNowOnly}
                setOpenNowOnly={setOpenNowOnly}
                skill={skill}
                setSkill={setSkill}
              />
            </div>
          </div>

          {view === "Map" ? (
            <CourtMap
              courts={filtered}
              computedById={computedById}
              selectedId={selectedId}
              onSelect={setSelectedId}
            />
          ) : (
            <CourtList
              courts={filtered}
              computedById={computedById}
              selectedId={selectedId}
              onSelect={setSelectedId}
            />
          )}
        </div>
      </main>

      <CourtDrawer
        open={!!selected}
        court={selected}
        computed={selected ? computedById[selected.id] : null}
        onClose={() => setSelectedId(null)}
      />
    </div>
  );
}
